function [value, value2] = hsvd(y,hrow)H = [];I = [];#H = hankel(y)h = hankelex(y,hrow);[U, S, V] = svd(h,0);Diag = diag(S);r = size(Diag);# Componentes de baja frecuencia H = Diag(1,1)*U(1:end,1)*V(1:end,1)';B = [H(1,1) (H(1,2:end) + H(2,1:end-1))/2 H(2,end)];# Componentes de alta frecuenciaI = Diag(2,1)*U(1:end,2)*V(1:end,2)';A = [I(1,1) (I(1,2:end) + I(2,1:end-1))/2 I(2,end)];value = B;value2 = A;X = B+A;end